/* AANPASSINGEN AAN HUUROVEREENKOMST VOOR REPARATIE-TABEL */

/* Alle huurovereenkomst van consoles die niet verkocht zijn worden voor reparatie aangeboden.*/

UPDATE HUUROVEREENKOMST
SET HUUROVEREENKOMST.SCHADE = 1  
WHERE HUUROVEREENKOMST.BARCODE NOT IN
	(SELECT BARCODE FROM KOOPOVEREENKOMST)
	AND HUUROVEREENKOMST.BARCODE < 10000060

/* Alle consoles zijn reparabel */
UPDATE HUUROVEREENKOMST
SET HUUROVEREENKOMST.REPARABEL = 1  
WHERE HUUROVEREENKOMST.BARCODE NOT IN
	(SELECT BARCODE FROM KOOPOVEREENKOMST)
	AND HUUROVEREENKOMST.BARCODE < 10000060

/* Enkele consoles zijn niet reparabel */
UPDATE HUUROVEREENKOMST
SET HUUROVEREENKOMST.REPARABEL = NULL  
WHERE HUUROVEREENKOMST.BARCODE IN
	(10000005, 10000006, 10000013)

