/* 
Inkoopprijs is 50% van de verkoopprijs.
Inkoopprijs is + 25% wanneer gekozen wordt voor BONNEN.
*/
USE GAMEPARADISE

DECLARE @cnt INT = 10000000;

WHILE @cnt < 10000300 
BEGIN
	UPDATE INKOOPOVEREENKOMST 
	SET INKOOPOVEREENKOMST.INKOOPBEDRAG = (SELECT ARTIKEL.PRIJS FROM ARTIKEL WHERE ARTIKEL.BARCODE = @cnt) / 2
	WHERE INKOOPOVEREENKOMST.BARCODE = @cnt AND @cnt IN (SELECT BARCODE FROM ARTIKEL)
	SET @cnt = @cnt + 1;
END;

DECLARE @cnt1 INT = 10000000;

WHILE @cnt1 < 10000300 
BEGIN
	UPDATE INKOOPOVEREENKOMST 
	SET INKOOPOVEREENKOMST.INKOOPBEDRAG = INKOOPOVEREENKOMST.INKOOPBEDRAG * 1.25
	WHERE INKOOPOVEREENKOMST.BARCODE = @cnt1 AND INKOOPOVEREENKOMST.UITBETALING_CASH_OF_BONNEN = 'BONNEN'
	SET @cnt1 = @cnt1 + 1;
END;

/*
VerKOOP van een artikel mag niet op een datum die voor de verhuur-EINDdatum van een artikel ligt.
*/

USE GAMEPARADISE

DECLARE @cnt2 INT = 10000000;

WHILE @cnt2 < 10000300 
BEGIN
	UPDATE KOOPOVEREENKOMST 
	SET KOOPOVEREENKOMST.DATUM = DATEADD(DAY, FLOOR(RAND()*(14)), (SELECT MAX (HUUROVEREENKOMST.EINDDATUM) FROM HUUROVEREENKOMST WHERE HUUROVEREENKOMST.BARCODE = @cnt2))
	WHERE (KOOPOVEREENKOMST.BARCODE = @cnt2) AND (@cnt2 IN 
		(SELECT DISTINCT H.BARCODE FROM HUUROVEREENKOMST H INNER JOIN KOOPOVEREENKOMST K ON H.BARCODE = K.BARCODE WHERE H.EINDDATUM > K.DATUM))
	SET @cnt2 = @cnt2 + 1;
END;


/*
VerKOOP van een artikel mag niet op een datum die voor de inkoopdatum van een artikel ligt.
*/

USE GAMEPARADISE

DECLARE @cnt3 INT = 10000000;

WHILE @cnt3 < 10000300 
BEGIN
	UPDATE INKOOPOVEREENKOMST 
	SET INKOOPOVEREENKOMST.DATUM = DATEADD(DAY, FLOOR(RAND()*(-14)), (SELECT KOOPOVEREENKOMST.DATUM FROM KOOPOVEREENKOMST WHERE KOOPOVEREENKOMST.BARCODE = @cnt3))
	WHERE (INKOOPOVEREENKOMST.BARCODE = @cnt3) AND (@cnt3 IN 
		(SELECT DISTINCT K.BARCODE FROM KOOPOVEREENKOMST K INNER JOIN INKOOPOVEREENKOMST I ON K.BARCODE = I.BARCODE WHERE K.DATUM < I.DATUM))
	SET @cnt3 = @cnt3 + 1;
END;

